import * as React from "react"
import { PropertyControls } from "../render/types/PropertyControls"

/**
 * Extends component with property controls
 *
 * ```typescript
 * export const MyComponent = props => <h1>{props.header}</h1>
 *
 * addPropertyControls(MyComponent, {
 *   header:  { type: ControlType.String, title: "Header" },
 * })
 *
 * ```
 * @public
 */
export function addPropertyControls<Props = any>(
    component: React.ComponentType<Props> | React.ForwardRefExoticComponent<Props>,
    propertyControls: PropertyControls<Props>
) {
    Object.assign(component, { propertyControls })
}

/**
 * Get the property controls for a component
 * @param component - The component to retrieve the property controls for
 * @returns The property controls for the given component
 * @internal
 */
export function getPropertyControls<Props = any>(
    component: React.ComponentType<Props> | React.ForwardRefExoticComponent<Props>
): PropertyControls<Props> | undefined {
    // We need to cast the component here because the function accepts any component type by design
    return (component as any).propertyControls
}
import type { EmulatedScrollProps } from "./types"

import * as React from "react"
import { useMemo, useRef, useCallback } from "react"
import { useMotionValue, useDragControls, DraggableProps, PanInfo, BoundingBox2D, MotionValue } from "framer-motion"
import sync from "framesync"
import { FrameWithMotion } from "../../render/presentation/Frame/FrameWithMotion"
import { EmptyState } from "../EmptyState"
import { isMotionValue } from "../../render/utils/isMotionValue"
import { RenderTarget } from "../../render/types/RenderEnvironment"
import { isFiniteNumber } from "../../render/utils/isFiniteNumber"
import { useWheelScroll } from "./useWheelScroll"
import { useLayoutId } from "../../render/utils/useLayoutId"
import { useIsInCurrentNavigationTarget } from "../../index"
import { injectComponentCSSRules } from "../../render/utils/injectComponentCSSRules"

const directionMap: { [key: string]: DraggableProps["drag"] } = {
    horizontal: "x",
    vertical: "y",
    both: true,
}

function convertScrollDirectionToDrag(scrollDirection?: "horizontal" | "vertical" | "both") {
    return scrollDirection ? directionMap[scrollDirection] : scrollDirection
}

const useUpdateChildSize = ({
    dragDirection,
    children,
    fromCanvasComponent,
}: {
    dragDirection: "both" | "horizontal" | "vertical"
    children: React.ReactNode
    fromCanvasComponent?: boolean
}): React.ReactNode | React.ReactNode[] => {
    return useMemo((): React.ReactNode | React.ReactChild[] => {
        return React.Children.map(children, (child: React.ReactChild) => {
            if (child === null || typeof child !== "object" || typeof child.type === "string") {
                return child
            }

            const updatedSize: Partial<{ width: number | string; height: number | string }> = {}
            switch (dragDirection) {
                case "vertical":
                    updatedSize.width = "100%"
                    break
                case "horizontal":
                    updatedSize.height = "100%"
                    break
                default:
                    return child
            }

            const update = fromCanvasComponent
                ? { style: Object.assign({}, child.props.style, updatedSize) }
                : updatedSize
            return React.cloneElement(child, update)
        })
    }, [dragDirection, children])
}

const numberFromOptionalMotionValue = (value: number | MotionValue<number>): number => {
    return typeof value === "number" ? value : value.get()
}
