/**
 * @public
 */
export type UpdateFunction<Value> = (change: Change<Value>, transaction?: TransactionId) => void
/**
 * @public
 */
export type FinishFunction = (transaction: TransactionId) => void
/**
 * @public
 */
export type Observer<Value> =
    | {
          update: UpdateFunction<Value>
          finish: FinishFunction
      }
    | UpdateFunction<Value>

/**
 * @public
 */
export interface UpdateObserver<Value> {
    onUpdate(handler: Observer<Value>): Cancel
}

/**
 * @internal
 */
export class Observers<Value> {
    private observers: Set<Observer<Value>> = new Set()
    private transactions: { [key: number]: Change<Value> } = {}
    add(observer: Observer<Value>): Cancel {
        this.observers.add(observer)
        let isCalled = false
        return () => {
            if (isCalled) {
                return
            }
            isCalled = true
            this.remove(observer)
        }
    }

    private remove(observer: Observer<Value>) {
        this.observers.delete(observer)
    }

    notify(change: Change<Value>, transaction?: TransactionId) {
        if (transaction) {
            const accumulatedChange = this.transactions[transaction] || change
            accumulatedChange.value = change.value
            this.transactions[transaction] = accumulatedChange
        } else {
            this.callObservers(change)
        }
    }

    finishTransaction(transaction: TransactionId) {
        const accumulatedChange = this.transactions[transaction]
        delete this.transactions[transaction]
        return this.callObservers(accumulatedChange, transaction)
    }

    private callObservers(change: Change<Value>, transaction?: TransactionId) {
        const finishObservers: FinishFunction[] = []
        // Make a copy and de-duplicate so we always call all handlers,
        // even if the handler array changes because of handler call
        new Set(this.observers).forEach(observer => {
            if (typeof observer === "function") {
                observer(change, transaction)
            } else {
                observer.update(change, transaction)
                finishObservers.push(observer.finish)
            }
        })
        return finishObservers
    }
}
/**
 * Throws an error when the condition is falsy, optionally pass in a message to
 * describe the error.
 *
 * Best to use asserts where data is about to be stored. Then errors are caught
 * early, close to the cause. Otherwise data with broken expectation might hang
 * around, only to cause errors when used later on, with no pointer back to what
 * caused that bad state.
 *
 * Note that when an assert fails, consider that a fatal error caused by buggy
 * code. The program should halt, or perhaps do some kind of high level
 * recovery, and the bug should be reported and fixed. Asserts are not well
 * suited to use as defensive programming, but are good to use to quickly
 * highlight mistakes while writing new code.
 *
 * Also note asserts should not have side effects, the program should continue
 * to work when asserts are compiled away. Because asserts might be compiled
 * away in production code.
 *
 * And finally, both the condition should be cheap, and the message should not
 * do string interpolation, instead you can pass in multiple parts of the
 * message.
 */
export function assert(condition: any, ...msg: any[]): asserts condition {
    if (condition) return

    const e = Error("Assertion Error" + (msg.length > 0 ? ": " + msg.join(" ") : ""))
    // Hack the stack so the assert call itself disappears. Works in jest and in chrome.
    if (e.stack) {
        try {
            const lines = e.stack.split("\n")
            if (lines[1].includes("assert")) {
                lines.splice(1, 1)
                e.stack = lines.join("\n")
            } else if (lines[0].includes("assert")) {
                lines.splice(0, 1)
                e.stack = lines.join("\n")
            }
        } catch {
            // nothing
        }
    }
    throw e
}

/**
 * Asserts that a code path is never entered. Additionally, asserts that the provided
 * value is evaluated to `never` by the type system, which means all possible discrete
 * types have been exhaustively checked by the time this statement is reached.
 */
export function assertNever(x: never, error?: any): never {
    throw error || new Error(x ? `Unexpected value: ${x}` : "Application entered invalid state")
}
