export function externalModuleIdentifier(
    moduleId: GlobalModuleId,
    saveId: string,
    file: string,
    exportSpecifier?: string
): ExternalModuleIdentifier {
    const path = `${moduleId}/${saveId}/${file}`
    const importSpecifier = `${getServiceMap().modulesCDN}/${path}`
    if (exportSpecifier) {
        return {
            kind: "externalModuleExport",
            value: `module:${path}:${exportSpecifier}` as ExternalModuleExportIdentifierString,
            moduleId: asGlobalId(moduleId),
            saveId,
            file,
            importSpecifier,
            exportSpecifier,
        }
    } else {
        return {
            kind: "externalModule",
            value: `module:${path}` as ExternalModuleBareIdentifierString,
            moduleId: asGlobalId(moduleId),
            saveId,
            file,
            importSpecifier,
