import { assert, getLogger } from "./index"

const log = getLogger("task-queue")

/**
 * These queues use a single micro task and a single timer to batch small tasks into one large task.
 * Every queue has an associated delay, which is applied to all of its tasks. And every queue has a
 * priority associeted with it, when the overall runner is configured at a higher priority, lower
 * priority queues won't run any tasks at all.
 */

export interface TaskQueueOptions {
    delay?: number
    priority?: number
    maxBatchSize?: number
}

type WrapTaskRunner = (body: () => void) => void
type Task = () => void

class ScheduledTask {
    isCancelled = false

    constructor(public atTime: number, readonly task: Task) {}

    cancel() {
        this.isCancelled = true
    }
}

class TaskQueue {
    // TODO: ideally, this delay would mean tasks would be scheduled after the delay - cpu busy time
    readonly delay: number = 0
    readonly priority: number = 0
    readonly maxBatchSize: number = 0

    incoming: ScheduledTask[] = []
    scheduled: ScheduledTask[] = []

    constructor(private runner: TaskQueueRunner, readonly name: string, readonly options?: TaskQueueOptions) {
        this.delay = options?.delay ?? 0
        this.priority = options?.priority ?? 0
        this.maxBatchSize = options?.maxBatchSize ?? 0
    }

    add(task: Task): ScheduledTask {
        const scheduled = new ScheduledTask(-1, task)
        this.incoming.push(scheduled)
        this.runner.taskAdded()
        return scheduled
    }

    scheduleNewTasks(now: number) {
        if (this.incoming.length === 0) return

        log.debug("scheduling:", this.name, this.incoming.length)
        const atTime = now + this.delay
        for (const scheduled of this.incoming) {
            scheduled.atTime = atTime
            this.scheduled.push(scheduled)
        }
        this.incoming.length = 0
    }

    millisUntilNextTask(now: number): number {
        if (this.scheduled.length === 0) return Infinity
        return this.scheduled[0].atTime - now
    }

    run(now: number) {
        let count = this.scheduled.length
        if (count === 0) return

        if (this.delay > 0) {
            if (this.scheduled[count - 1].atTime > now) {
                count = this.scheduled.findIndex(task => task.atTime > now)
            }
        }

        if (this.maxBatchSize > 0 && count > this.maxBatchSize) {
            count = this.maxBatchSize
        }

        const toRun = this.scheduled.splice(0, count)
        log.debug("running:", this.name, toRun.length)
        for (let i = 0, il = toRun.length; i < il; i++) {
            const scheduled = toRun[i]
            if (scheduled.isCancelled) continue
            scheduled.task()
        }
    }
}

export class TaskQueueRunner {
    private wrapper: WrapTaskRunner = (body: () => void) => body()
    private queues: TaskQueue[] = []
    private currentPriority: number = 0

    setTaskWrapper(wrapper: WrapTaskRunner): this {
        this.wrapper = wrapper
        return this
    }

    setPriority(priority: number): this {
        if (priority === this.currentPriority) return this

        log.debug("set priority:", this.currentPriority, "->", priority)
        this.currentPriority = priority
        this.taskAdded()
        return this
    }

    getPriority(): number {
        return this.currentPriority
    }

    hasImmediateTasksToRun(): boolean {
        return this.millisUntilNextTask(performance.now()) === 0
    }

    getTaskQueue(name: string, options?: TaskQueueOptions) {
        const existing = this.queues.find(q => q.name === name)
        if (existing) {
            const same =
                existing.options?.delay === options?.delay &&
                existing.options?.priority === options?.priority &&
                existing.options?.maxBatchSize === options?.maxBatchSize
            assert(same, "queue", name, "with different options already exists")
            return existing
        }

        const queue = new TaskQueue(this, name, options)
        this.queues.push(queue)
        this.queues.sort((a, b) => a.priority - b.priority)
        return queue
    }

    microTask = false
    taskAdded() {
        if (this.microTask) return
        this.microTask = true
        queueMicrotask(this.scheduleNewTasks)
    }

    private scheduleNewTasks = () => {
        this.microTask = false
        const now = performance.now()
        this.queues.forEach(queue => {
            queue.scheduleNewTasks(now)
        })
        this.rescheduleRun()
    }

    private millisUntilNextTask(now: number): number {
        let until = Infinity
        this.queues.forEach(queue => {
            if (queue.priority < this.currentPriority) return
            until = Math.min(until, queue.millisUntilNextTask(now))
        })
        return Math.max(0, until)
    }

    private atTime: number = Infinity
    private timer: any

    private rescheduleRun() {
        const now = performance.now()
        const delay = this.millisUntilNextTask(now)
        if (!Number.isFinite(delay)) return

        const atTime = now + delay
        if (atTime > this.atTime) return

        if (this.timer) {
            clearTimeout(this.timer)
        }
        this.atTime = now + delay
        this.timer = setTimeout(this.run, delay)
    }

    /** Actually run tasks */
    private run = () => {
        this.atTime = Infinity
        this.timer = null

        const now = performance.now()
        this.wrapper(() => {
            this.queues.forEach(queue => {
                if (queue.priority < this.currentPriority) return
                queue.run(now)
            })
        })

        this.rescheduleRun()
    }
}
import { UpdateObserver, Observers, Observer, FinishFunction } from "./Observers"
import { Interpolation } from "../../interpolation/Interpolation"
import { MotionValue } from "framer-motion"
import { deprecationWarning } from "@framerjs/shared"

/** @public */
export type Cancel = () => void

/**
 * @public
 */
export type TransactionId = number
export interface Change<Value> {
    value: Value
    oldValue?: Value
}

export type ToAnimatable<PossiblyAnimatable> = PossiblyAnimatable extends Animatable<infer Value>
    ? Animatable<Value>
    : Animatable<PossiblyAnimatable>

export type FromAnimatable<PossiblyAnimatable> = PossiblyAnimatable extends Animatable<infer Value>
    ? Value
    : PossiblyAnimatable

export type ToAnimatableOrValue<PossiblyAnimatable> = PossiblyAnimatable extends Animatable<infer Value>
    ? Value | Animatable<Value>
    : PossiblyAnimatable | Animatable<PossiblyAnimatable>

/** @public */
export type AnimatableObject<T> = { [K in keyof T]: ToAnimatableOrValue<T[K]> }

/**
 * @public
 * @deprecated
 */
export type DeprecatedAnimationTarget<Value> = Animatable<Value> | AnimatableObject<Value> | MotionValue<Value>

/**
 * @public
 * @deprecated Use {@link useMotionValue} instead
 */
export interface Animatable<Value> extends UpdateObserver<Value> {
    /**
     * Get the current value out of this Animatable object
     * @remarks
     * ```jsx
     * const a = Animatable(0)
     * a.get() // returns 0
     * await animate(a, 42)
     * a.get() // returns 42
     * ```
     * @returns Current value
     * @public
     */
    get(): Value
    /**
     * Set a new value to a animatable object
     * @remarks
     * The passed value can be an Animatable value too
     * ```jsx
     * const a = Animatable(0)
     * const b = Animatable(100)
     * a.set(42)
     * a.get() // returns 42
     * a.set(b)
     * a.get() // returns 100
     * ```
     * @param value - New value to set to the animatable
     * @public
     */
    set(value: Value | Animatable<Value>): void
    /**
     * @public
     */
    set(value: Value | Animatable<Value>, transaction?: TransactionId): void
    /**
     * @internal
     */
    finishTransaction(transaction: TransactionId): FinishFunction[]
}

/**
 * Creates a Animatable object that can be animated. These objects can be passed into a {@link DeprecatedFrame} instead of a primitive like number
 * and afterwards animated with {@link (animate:function)}.
 * @remarks
 * ```jsx
 * const value = Animatable(0)
 * animate(value, 100)
 * ```
 * @param value - Value to animate
 * @returns Animatable value
 * @public
 * @deprecated Use {@link useMotionValue} instead
 */
export function Animatable<Value>(value: Value | Animatable<Value>): Animatable<Value> {
    deprecationWarning("Animatable()", "2.0.0", "the new animation API (https://www.framer.com/api/animation/)")
    return isAnimatable(value) ? value : new AnimatableValue(value)
}

/**
 * @public
 */
export namespace Animatable {
    /**
     * @internal
     */
    export function transaction(
        update: (updater: (animatable: Animatable<any>, value: any) => void, transactionId: TransactionId) => void
    ): void {
        const transactionId = Math.random()
        const updatedValues: Set<Animatable<any>> = new Set()
        const updater = (animatable: Animatable<any>, value: any): void => {
            animatable.set(value, transactionId)
            updatedValues.add(animatable)
        }
        update(updater, transactionId)
        const finishObservers: FinishFunction[] = []
        updatedValues.forEach(value => {
            finishObservers.push(...value.finishTransaction(transactionId))
        })
        finishObservers.forEach(finish => {
            finish(transactionId)
        })
    }

    /**
     * @public
     */
    export function getNumber(value: number | Animatable<number> | null | undefined, defaultValue: number = 0): number {
        return Animatable.get(value, defaultValue)
    }

    /** @internal */
    export function get<Value>(value: Value | Animatable<Value> | null | undefined, defaultValue: Value): Value {
        if (value === undefined || value === null) {
            return defaultValue
        }
        if (isAnimatable(value)) {
            return value.get()
        }
        return value
    }

    /**
     * @internal
     */
    export function objectToValues<Object>(object: AnimatableObject<Object>): Object {
        if (!object) {
            return object
        }
        const result: any = {}
        for (const key in object) {
            const value = object[key]
            if (isAnimatable(value)) {
                result[key] = value.get()
            } else {
                result[key] = value
            }
        }
        return result
    }
}

const onUpdateKey: keyof AnimatableValue = "onUpdate"
const finishTransactionKey: keyof AnimatableValue = "finishTransaction"

/**
 * @internal
 * @deprecated
 */
export function isAnimatable(value: any): value is Animatable<any> {
    return (
        value !== null &&
        typeof value === "object" &&
        onUpdateKey in value &&
        value[onUpdateKey] instanceof Function &&
        finishTransactionKey in value &&
        value[finishTransactionKey] instanceof Function
    )
}

function animatableInterpolation<Value>(
    value: Animatable<Value>,
    currentInterpolation: Interpolation<any>
): Interpolation<Animatable<Value>> {
    return {
        interpolate(from: Animatable<Value>, to: Animatable<Value>): (progress: number) => Animatable<Value> {
            const fromValue = from.get()
            const toValue = to.get()
            const result = Animatable(fromValue)

            return (progress: number): Animatable<any> => {
                const v = currentInterpolation.interpolate(fromValue, toValue)(progress)
                result.set(v)
                return result
            }
        },
        difference(from: Animatable<Value>, to: Animatable<Value>): number {
            const v = from.get()
            return currentInterpolation.difference(v, to.get())
        },
    }
}

class AnimatableValue<Value = any> implements Animatable<Value> {
    private observers = new Observers()

    constructor(private value: Value) {}

    static interpolationFor<Value>(
        value: any,
        currentInterpolation: Interpolation<any>
    ): Interpolation<Animatable<Value>> | undefined {
        if (isAnimatable(value)) {
            return animatableInterpolation<Value>(value, currentInterpolation)
        }
    }

    get(): Value {
        return this.value
    }

    set(value: Value | Animatable<Value>, transaction?: TransactionId) {
        const oldValue = this.value
        if (isAnimatable(value)) {
            value = value.get()
        }
        this.value = value
        const change = {
            value,
            oldValue,
        }
        this.observers.notify(change, transaction)
    }

    finishTransaction(transaction: TransactionId) {
        return this.observers.finishTransaction(transaction)
    }

    onUpdate(handler: Observer<Value>): Cancel {
        return this.observers.add(handler)
    }
}
