// Constraint system that can switch between DOM and Constraint layout

export interface PositionProperties {
    top: number | string
    right: number | string
    bottom: number | string
    left: number | string
    center: "x" | "y" | boolean
}

export interface SizeProperties {
    width: number | string
    height: number | string
    minWidth?: number | string
    minHeight?: number | string
    size: number | string
}

/** @public */
export interface PositionStickyProperties {
    /** @internal */
    positionSticky?: boolean
    /** @internal */
    positionStickyTop?: number
    /** @internal */
    positionStickyRight?: number
    /** @internal */
    positionStickyBottom?: number
    /** @internal */
    positionStickyLeft?: number
}

export interface LayoutProperties extends PositionProperties, PositionStickyProperties, SizeProperties {
    /** @internal */
    widthType?: DimensionType
    /** @internal */
    heightType?: DimensionType
}

export interface CustomConstraintProperties {
    /**
     * Aspect Ratio to keep when resizing
     * @public
     */
    aspectRatio?: number | null

    /**
     * Used for Text and Graphics containers
     * @public
     */
    autoSize?: boolean

    /**
     * Use Vekter constraint layout system, disable DOM layout
     * @public
     */
    enabled: boolean

    intrinsicWidth?: number
    intrinsicHeight?: number
}

export interface ConstraintConfiguration {
    /** @internal */
    _constraints: CustomConstraintProperties
}

/** @internal */
export interface NewConstraintProperties extends Partial<LayoutProperties>, ConstraintConfiguration {}

function isString(t: any): t is string {
    return typeof t === "string"
}

// The old constraint system does not allow for strings, so this function checks if the layout props contain string values
// In certain centering cases we can convert the strings to the old constraint system, so that logic is captured here
function containsInvalidStringValues(props: Partial<NewConstraintProperties>): boolean {
    const { left, right, top, bottom, center } = props
    // We never allow right or bottom to be strings
    if ([right, bottom].some(isString)) {
        return true
    }
    // Only allow a string for left, if it is part of the centering logic
    if (isString(left) && (!center || center === "y")) {
        // We are not centering or only centering in the opposite direction
        return true
    }
    // Only allow a string for top, if it is part of the centering logic
    if (isString(top) && (!center || center === "x")) {
        // We are not centering or only centering in the opposite direction
        return true
    }
    return false
}

/** @internal */
export function constraintsEnabled(props: Partial<NewConstraintProperties>): props is NewConstraintProperties {
    const { _constraints } = props
    if (!_constraints) {
        return false
    }

    if (containsInvalidStringValues(props)) {
        return false
    }

    return _constraints.enabled
}

function sizeFromFiniteNumberProps(props: Partial<NewConstraintProperties>): Size | null {
    const { size } = props
    let { width, height } = props
    if (isFiniteNumber(size)) {
        if (width === undefined) {
            width = size
        }
        if (height === undefined) {
            height = size
        }
    }
    if (isFiniteNumber(width) && isFiniteNumber(height)) {
        return {
            width: width,
            height: height,
        }
    }
    return null
}

function rectFromFiniteNumberProps(props: Partial<NewConstraintProperties>): Rect | null {
    const size = sizeFromFiniteNumberProps(props)
    if (size === null) {
        return null
    }
    const { left, top } = props
    if (isFiniteNumber(left) && isFiniteNumber(top)) {
        return {
            x: left,
            y: top,
            ...size,
        }
    }
    return null
}

export function calculateSize(
    props: Partial<NewConstraintProperties & Partial<{ size: number | string }>>,
    parentSize: ParentSize
): Size | null {
    if (
        !constraintsEnabled(props) ||
        parentSize === ParentSizeState.Disabled ||
        parentSize === ParentSizeState.DisabledForCurrentLevel
    ) {
        return sizeFromFiniteNumberProps(props)
    }
    const constraintValues = getConstraintValues(props)
    const pixelAlign = true
    const rect = ConstraintValues.toRect(constraintValues, deprecatedParentSize(parentSize), null, pixelAlign)
    return { width: rect.width, height: rect.height }
}

/** @internal */
export function calculateRect(
    props: Partial<NewConstraintProperties & Partial<{ size: number | string }>>,
    parentSize: ParentSize,
    pixelAlign: boolean = true
): Rect | null {
    if (
        !constraintsEnabled(props) ||
        parentSize === ParentSizeState.Disabled ||
        parentSize === ParentSizeState.DisabledForCurrentLevel
    ) {
        return rectFromFiniteNumberProps(props)
    }
    const constraintValues = getConstraintValues(props)
