export function stringToObject(inputColor: string): ColorTypeRGBA | ColorTypeHSLA | ColorTypeHSVA | false {
    // Early return for gradients
    if (inputColor.includes("gradient(")) return false

    // Early return for variables
    if (inputColor.includes("var(")) return false

    const trimLeft = /^[\s,#]+/
    const trimRight = /\s+$/

    let color = inputColor.replace(trimLeft, "").replace(trimRight, "").toLowerCase()
    let named = false
    if (cssNames[color]) {
        color = cssNames[color]
        named = true
    }
    if (color === "transparent") {
        return {
            r: 0,
            g: 0,
            b: 0,
            a: 0,
            format: ColorFormat.NAME,
        }
    }
    let match
    if ((match = matchers.rgb.exec(color))) {
        return {
            r: parseInt(match[1]),
            g: parseInt(match[2]),
            b: parseInt(match[3]),
            a: 1,
            format: ColorFormat.RGB,
        } as ColorTypeRGBA
    }
    if ((match = matchers.rgba.exec(color))) {
        return {
            r: parseInt(match[1]),
            g: parseInt(match[2]),
            b: parseInt(match[3]),
            a: parseFloat(match[4]),
            format: ColorFormat.RGB,
        } as ColorTypeRGBA
    }
    if ((match = matchers.hsl.exec(color))) {
        return {
            h: parseInt(match[1]),
            s: percentToFraction(match[2]),
            l: percentToFraction(match[3]),
            a: 1,
            format: ColorFormat.HSL,
        } as ColorTypeHSLA
    }
    if ((match = matchers.hsla.exec(color))) {
        return {
            h: parseInt(match[1]),
            s: percentToFraction(match[2]),
            l: percentToFraction(match[3]),
            a: parseFloat(match[4]),
            format: ColorFormat.HSL,
        } as ColorTypeHSLA
    }
    if ((match = matchers.hsv.exec(color))) {
        return {
            h: parseInt(match[1]),
            s: percentToFraction(match[2]),
            v: percentToFraction(match[3]),
            a: 1,
            format: ColorFormat.HSV,
        } as ColorTypeHSVA
    }
    if ((match = matchers.hsva.exec(color))) {
        return {
            h: parseInt(match[1]),
            s: percentToFraction(match[2]),
            v: percentToFraction(match[3]),
            a: parseFloat(match[4]),
            format: ColorFormat.HSV,
        } as ColorTypeHSVA
    }
    if ((match = matchers.hex8.exec(color))) {
        return {
            r: parseIntFromHex(match[1]),
            g: parseIntFromHex(match[2]),
            b: parseIntFromHex(match[3]),
            a: convertHexToDecimal(match[4]),
            format: named ? ColorFormat.NAME : ColorFormat.HEX,
        } as ColorTypeRGBA
    }
    if ((match = matchers.hex6.exec(color))) {
        return {
            r: parseIntFromHex(match[1]),
            g: parseIntFromHex(match[2]),
            b: parseIntFromHex(match[3]),
            a: 1,
            format: named ? ColorFormat.NAME : ColorFormat.HEX,
        } as ColorTypeRGBA
    }
    if ((match = matchers.hex4.exec(color))) {
        return {
            r: parseIntFromHex(`${match[1]}${match[1]}`),
            g: parseIntFromHex(`${match[2]}${match[2]}`),
            b: parseIntFromHex(`${match[3]}${match[3]}`),
            a: convertHexToDecimal(match[4] + "" + match[4]),
            format: named ? ColorFormat.NAME : ColorFormat.HEX,
        } as ColorTypeRGBA
    }
    if ((match = matchers.hex3.exec(color))) {
        return {
            r: parseIntFromHex(`${match[1]}${match[1]}`),
            g: parseIntFromHex(`${match[2]}${match[2]}`),
            b: parseIntFromHex(`${match[3]}${match[3]}`),
            a: 1,
            format: named ? ColorFormat.NAME : ColorFormat.HEX,
        } as ColorTypeRGBA
    } else {
        return false
    }
}

function parseIntFromHex(hex: string): number {
    return parseInt(hex, 16)
}

function convertHexToDecimal(h: string): number {
    return parseIntFromHex(h) / 255
