export const isBrowser = () => typeof document === "object"

export const isWebKit = () => navigator.userAgent.includes("AppleWebKit/") && !isChrome() && !isEdge()

export const webkitVersion = () => {
    let version = -1
    const regexp = /AppleWebKit\/([\d.]+)/
    const result = regexp.exec(navigator.userAgent)
    if (result) {
        version = parseFloat(result[1])
    }
    return version
