    const size = cache.size
    if (size < maxEntries) return

    // randomly start at 0 or 1, delete every other key in a fast way
    let i = Math.round(Math.random())
    for (const key of cache.keys()) {
        if ((++i & 0x1) === 0x1) continue
        cache.delete(key)
    }
