import { EventEmitter } from "./EventEmitter"

export type LoopEventNames = "render" | "update" | "finish"
export type LoopDeltaCallback = (this: Loop, frame: number) => void

let LoopTimeStep = 1 / 60

/**
 * @public
 */
export class Loop extends EventEmitter<LoopEventNames> {
    private _started = false
    private _frame = 0
    private _frameTasks: Function[] = []

    /**
     * To add a task to be done at the end of a frame.
     * Tasks added from a task will be ignored. These will run after loop events have been processed.
     * @internal
     */
    addFrameTask(task: Function) {
        this._frameTasks.push(task)
    }

    private _processFrameTasks() {
        const postEventTasks = this._frameTasks
        const length = postEventTasks.length
        if (length === 0) return

        for (let i = 0; i < length; i++) {
            postEventTasks[i]()
        }

        postEventTasks.length = 0
    }

    /**
     * @internal
     */
    static set TimeStep(value: number) {
        LoopTimeStep = value
    }

    /**
     * @internal
     */
    static get TimeStep() {
        return LoopTimeStep
    }

    /**
     * @internal
     */
    constructor(start = false) {
        super()
        if (start) {
            this.start()
        }
    }

    /**
     * @internal
     */
    start(): this {
        if (this._started) return this

        this._frame = 0
        this._started = true
        time.raf(this.tick)
        return this
    }

    /**
     * @internal
     */
    stop(): this {
        this._started = false
        return this
    }

    /**
     * @internal
     */
    get frame() {
        return this._frame
    }

    /**
     * @internal
     */
    get time() {
        return this._frame * LoopTimeStep
    }

    /**
     * @internal
     */
    private tick = () => {
        if (!this._started) return

        time.raf(this.tick)

        this.emit("update", this._frame, LoopTimeStep)
        this.emit("render", this._frame, LoopTimeStep)
        this._processFrameTasks()

        this._frame++
    }
}

/**
 * @internal
 */
export const MainLoop = new Loop()
export const round = (value: number, decimals = 0, increment = 1, min?: number, max?: number) => {
    const d = Math.pow(10, decimals)

    if (increment) {
        value = Math.round(value / increment) * increment
    }
    value = Math.round(value * d) / d

    if (min && value < min) {
        return min
    }
    if (max && value > max) {
        return max
    }
    return value
}

export const roundWhole = (value: number, decimals = 1) => {
    // Return integer if whole value, else include decimals
    if (decimals === null) {
        decimals = 1
    }
    if (parseInt(value.toString()) === value) {
        return parseInt(value.toString())
    }
    return round(value, decimals)
}

export const clamp = (value: number, a: number, b: number) => {
    const min = Math.min(a, b)
    const max = Math.max(a, b)

    if (value < min) {
        value = min
    }
    if (value > max) {
        value = max
    }
    return value
}

// Taken from http://jsfiddle.net/Xz464/7/
// Used by animation engine, needs to be very performant
// export const mapRange = (value, fromLow, fromHigh, toLow, toHigh) => {
// 	return toLow + (((value - fromLow) / (fromHigh - fromLow)) * (toHigh - toLow))
// }

// Kind of similar as above but with a better syntax and a limiting option
export const modulate = (value: number, rangeA: [number, number], rangeB: [number, number], shouldClamp = false) => {
    const [fromLow, fromHigh] = Array.from(rangeA)
    const [toLow, toHigh] = Array.from(rangeB)

    // if rangeB consists of Colors we return a color tween
    // if Color.isColor(toLow) or _.isString(toLow) and Color.isColorString(toLow)
    // 	ratio = Utils.modulate(value, rangeA, [0, 1])
    // 	result = Color.mix(toLow, toHigh, ratio)
    // 	return result

    const result = toLow + ((value - fromLow) / (fromHigh - fromLow)) * (toHigh - toLow)

    if (shouldClamp === true) {
        if (toLow < toHigh) {
            if (result < toLow) {
                return toLow
            }
            if (result > toHigh) {
                return toHigh
            }
        } else {
            if (result > toLow) {
                return toLow
            }
            if (result < toHigh) {
                return toHigh
            }
        }
    }

    return result
}
