/**
 * @public
 */
export class FramerEvent {
    /**
     * @internal
     */
    readonly time = Date.now()
    /**
     * @internal
     */
    readonly loopTime = MainLoop.time
    /**
     * @internal
     */
    readonly point: Point
    /**
     * @internal
     */
    readonly devicePoint: Point
    /**
     * @internal
     */
    readonly target: EventTarget | null
    /**
     * @internal
     */
    readonly delta: Point
