            this.propsObserverCancel = ObservableObject.addObserver(this.propsObserver, this.onPropsChange)
            if (
                this.props.parentSize &&
                isAnimatable(this.props.parentSize.width) &&
                isAnimatable(this.props.parentSize.height)
