    const animations = isLayeredContainer
        ? {
              initial: { ...identity, ...initialProps } as any,
              animate: { ...identity, ...transitionProps, transition } as any, // Overlay animations are sometimes instant
              exit: { ...identity, ...exitProps, transition: animate } as any, // Overlay exits are always animated
