            {isLayeredContainer && (
                <FrameWithMotion
                    width="100%"
                    height="100%"
                    transition={animation}
                    initial={{ opacity: instant && visible ? 1 : 0 }} // To prevent flashing
                    animate={{ opacity: 1 }}
                    exit={{ opacity: 0 }}
