                <NavigationContainerContext.Provider value={isCurrentTarget}>
                    <NavigationTargetWrapper isCurrent={isCurrentTarget} isOverlayed={isOverlayed}>
                        <SharedLayoutTree
                            isLead={isCurrent}
                            animatesLayout={!!withMagicMotion}
                            transition={transition}
                            isExiting={!isPresent}
                            id={id}
