            dragDirectionLock={directionLock}
            _dragX={contentOffsetRef.current.x}
            _dragY={contentOffsetRef.current.y}
            dragConstraints={constraintsRef.current}
            onDrag={onDrag}
            onDragStart={onDragStart}
            onDragEnd={onDragEnd}
