            top: paddingTop !== undefined ? paddingTop : padding,
            bottom: paddingBottom !== undefined ? paddingBottom : padding,
            left: paddingLeft !== undefined ? paddingLeft : padding,
            right: paddingRight !== undefined ? paddingRight : padding,
        }
    }
    return {
        top: padding,
        bottom: padding,
        left: padding,
        right: padding,
    }
}

/**
