/**
 * @internal
 */
export function imageUrlForAsset(asset: string, size?: number) {
    if (/^\w+:/.test(asset) && !isAssetReference(asset)) return asset

    if (typeof size !== "number") size = undefined
    else if (size <= 512) size = 512
    else if (size <= 1024) size = 1024
    else if (size <= 2048) size = 2048
    else size = 4096

    const isExport = RenderTarget.current() === RenderTarget.export
    return runtime.assetResolver(asset, { size, isExport }) ?? ""
}
import { Size } from "../types/Size"
import { ImagePatternElementProperties } from "../presentation/ImagePatternElement"
import { Background } from "../traits/Background"
import { BackgroundImage } from "../types/BackgroundImage"
import { Animatable } from "../../animation/Animatable/Animatable"

/** @internal */
export function imagePatternPropsForFill(
    fill: Background | Animatable<Background>,
    frame: Size,
    id: string
): ImagePatternElementProperties | undefined {
    fill = Animatable.get(fill, "#09F")

    if (!BackgroundImage.isImageObject(fill)) return undefined
    if (!fill.pixelWidth || !fill.pixelHeight) return undefined

    const imageWidth = fill.pixelWidth
    const imageHeight = fill.pixelHeight

    let transform: string | undefined
    const { fit } = fill

    if (fit === "fill" || fit === "fit" || !fit) {
        let scaleX = 1
        let scaleY = 1
        let offsetX = 0
        let offsetY = 0

        const imageRatio = imageWidth / imageHeight
        const realWidth = frame.height * imageRatio
        const realHeight = frame.width / imageRatio
        const validScaleX = realWidth / frame.width
        const validScaleY = realHeight / frame.height

        if (fit === "fill" || !fit ? validScaleY > validScaleX : validScaleY < validScaleX) {
            scaleY = validScaleY
            offsetY = (1 - validScaleY) / 2
        } else {
            scaleX = validScaleX
            offsetX = (1 - validScaleX) / 2
        }

        transform = `translate(${offsetX}, ${offsetY}) scale(${scaleX}, ${scaleY})`
    }

    const imageId = `id${id}g${"-fillImage"}`
    return { id: imageId, path: fill.src, transform }
}
