/**
 * @internal
 */
export function withPath(target: any): target is WithPath {
    return key in target
}

/**
 * @internal
 */
export type WithPaths = WithPath[]

/**
 * @internal
 */
export const pathDefaults: WithPath = {
    pathSegments: [],
    pathClosed: false,
}

/**
 * @internal
 */
export function toSVGPath(
    withPaths: WithPaths | WithPath,
    translate: { x: number; y: number } = { x: 0, y: 0 },
    canvasMode: RenderTarget = RenderTarget.canvas
): string {
    let pathElements: (number | string)[] = []

    let paths: WithPaths = []

    if (Array.isArray(withPaths)) {
        paths = withPaths
    } else {
        paths = [withPaths]
    }

    paths.forEach((path, index) => {
        const { pathClosed, pathSegments } = path
        const segmentCount = pathSegments.length
        if (segmentCount === 0) return ""

        for (let i = 0; i < segmentCount; i++) {
            const segment = pathSegments[i]
            let nextSegment: PathSegment | undefined
            let prevSegment: PathSegment | undefined

            const isFirstSegment = i === 0
            const isLastSegment = i === segmentCount - 1

            if (!isLastSegment) {
                nextSegment = pathSegments[i + 1]
            } else if (pathClosed) {
                nextSegment = pathSegments[0]
            }

            if (!isFirstSegment) {
                prevSegment = pathSegments[i - 1]
            } else if (pathClosed) {
                prevSegment = pathSegments[segmentCount - 1]
            }

            if (i === 0) {
                pathElements.push("M")
            } else if (prevSegment && isStraightCurve(prevSegment, segment)) {
                pathElements.push("L")
            }

            pathElements.push(segment.x + translate.x, segment.y + translate.y)

            if (nextSegment && !isStraightCurve(segment, nextSegment)) {
                const handleOut = PathSegment.calculatedHandleOut(segment)
                const handleIn = PathSegment.calculatedHandleIn(nextSegment)
                pathElements.push(
                    "C",
                    handleOut.x + translate.x,
                    handleOut.y + translate.y,
                    handleIn.x + translate.x,
                    handleIn.y + translate.y
                )
            }

            if (isLastSegment && nextSegment) {
                if (isStraightCurve(segment, nextSegment)) {
                    pathElements.push("Z")
                } else {
                    pathElements.push(nextSegment.x + translate.x, nextSegment.y + translate.y, "Z")
                }
            }
        }
    })
    if (canvasMode === RenderTarget.export || canvasMode === RenderTarget.preview) {
        pathElements = pathElements.map(value => (isFiniteNumber(value) ? roundedNumberString(value, 3) : value))
    }
    return pathElements.join(" ")
}

/**
 * @internal
 */
export function isStraightCurve(fromSegment: PathSegment, toSegment: PathSegment): boolean {
    const fromStraight =
        fromSegment.handleMirroring === "straight" || (fromSegment.handleOutX === 0 && fromSegment.handleOutY === 0)
    const toStraight =
        toSegment.handleMirroring === "straight" || (toSegment.handleInX === 0 && toSegment.handleInY === 0)
    return fromStraight && toStraight
}
/**
 * @public
 */
interface Size {
    width: number
    height: number
}

/**
 * @public
 */
function Size(width: number, height: number): Size {
    return { width, height }
}

/**
 * @public
 */
namespace Size {
    /**
     * @param sizeA -
     * @param sizeB -
     * @alpha
     */
    export const equals = (sizeA: Size | null, sizeB: Size | null) => {
        if (sizeA === sizeB) return true
        if (!sizeA || !sizeB) return false
        return sizeA.width === sizeB.width && sizeA.height === sizeB.height
    }

    /**
     *
     * @param fromSize - The initial size
     * @param toSize - The size to update to
     * @param keepAspectRatio - If the updating should preserve the aspect ratio
     * @remarks
     * keepAspectRatio only works if passing a toSize with only a width or height
     * @alpha
     */
    export const update = (fromSize: Size, toSize: Partial<Size>, keepAspectRatio = false) => {
        let { width, height } = fromSize
        const sizeRatio = width / height

        // Update from partial
        width = toSize.width !== undefined ? toSize.width : width
        height = toSize.height !== undefined ? toSize.height : height

        // Overwrite if we want and can keep the aspect ratio
        if (keepAspectRatio) {
            if (toSize.width === undefined && toSize.height !== undefined) {
                width = toSize.height * sizeRatio
            }
            if (toSize.width !== undefined && toSize.height === undefined && sizeRatio !== 0) {
                height = toSize.width / sizeRatio
            }
        }

        return { width, height }
    }

    /**
     *
     * @param sizeA -
     * @param sizeB -
     * @alpha
     */
    export function subtract(sizeA: Size, sizeB: Size) {
        return {
            width: Math.max(0, sizeA.width - sizeB.width),
            height: Math.max(0, sizeA.height - sizeB.height),
        }
    }

    /**
     * @public
     */
    export const zero = Size(0, 0)

    /**
     * Checks if the size has a zero width and zero height
     * @param size - size to check
     * @public
     */
    export const isZero = function (size: Size) {
        return size === Size.zero || (size.width === 0 && size.height === 0)
    }

    /**
     * @param width -
     * @param height -
     * @param size -
     * @alpha
     */
    export const defaultIfZero = function (width: number, height: number, size: Size) {
        if (isZero(size)) {
            return Size(width, height)
        } // else
        return size
    }
}

export { Size }
import { Point } from "./Point"

/**
 * @alpha
 */
interface Line {
    a: Point
    b: Point
}

/**
 * @alpha
 */
function Line(a: Point, b: Point): Line {
    return { a, b }
}

/**
 * @alpha
 */
namespace Line {
    export function intersection(lineA: Line, lineB: Line): Point | null {
        const x1 = lineA.a.x
        const y1 = lineA.a.y
        const x2 = lineA.b.x
        const y2 = lineA.b.y
        const x3 = lineB.a.x
        const y3 = lineB.a.y
        const x4 = lineB.b.x
        const y4 = lineB.b.y
        const d = (x1 - x2) * (y3 - y4) - (y1 - y2) * (x3 - x4)

        if (d === 0) {
            return null
        }

        const xi = ((x3 - x4) * (x1 * y2 - y1 * x2) - (x1 - x2) * (x3 * y4 - y3 * x4)) / d
        const yi = ((y3 - y4) * (x1 * y2 - y1 * x2) - (y1 - y2) * (x3 * y4 - y3 * x4)) / d
        return { x: xi, y: yi }
    }

    export const isOrthogonal = (line: Line) => {
        return line.a.x === line.b.x || line.a.y === line.b.y
    }

    export const perpendicular = (line: Line, pointOnLine: Point) => {
        const deltaX = line.a.x - line.b.x
        const deltaY = line.a.y - line.b.y
        const pointB = Point(pointOnLine.x - deltaY, pointOnLine.y + deltaX)
        return Line(pointB, pointOnLine)
    }

    export function projectPoint(line: Line, point: Point) {
        const perp = perpendicular(line, point)
        return intersection(line, perp)
    }
}

