        return (
            <pattern id={id} width="100%" height="100%" patternContentUnits="objectBoundingBox">
                <image
                    key={xlinkHref} // To make sure the xlinkHref gets updated correctly, else React updates href
                    width={1}
                    height={1}
                    xlinkHref={xlinkHref}
                    preserveAspectRatio="none"
                    transform={transform}
                />
            </pattern>
