// These types don’t do anything except make the code more readable
type Family = string
type Style = string
type Selector = string
type Weight = number | undefined
interface Font {
    selector: Selector
    weight: Weight
}

type TypefaceMembers = Record<Style, Font>
type Typefaces = Record<Family, TypefaceMembers>
// Parentheses for (slightly) improved readability only
type TypefaceDescriptors = [Family, [Style, Selector, Weight][]][]

function convertToObject(descriptors: TypefaceDescriptors): Typefaces {
    return Object.fromEntries(
        descriptors.map(descriptor => [
            descriptor[0],
            Object.fromEntries(
                descriptor[1].map(member => [
                    member[0],
                    {
                        selector: member[1],
                        weight: member[2],
                    },
                ])
            ),
        ])
    )
}

