/**
 * A mapped type that deeply changes all properties into readonly
 * all arrays into ReadonlyArray<T>
 * all maps into ReadonlyMap<T>
 */
type DeepReadonly<T> = {
    readonly [P in keyof T]: T[P] extends Map<infer K, infer V>
        ? ReadonlyMap<K, V>
        : T[P] extends (infer X)[]
        ? readonly X[]
        : DeepReadonly<T[P]>
}

