/**
 * @alpha
 */
export interface VectorGroupProps {
    name?: string
    opacity?: number | string
    visible: boolean
    x: number
    y: number
    rotation: number
    width: number
    height: number
    targetName?: string
    defaultName: string
    isRootVectorNode: boolean
    frame: Rect
    includeTransform?: boolean
}
/**
 * @alpha
 */
export interface VectorGroupProperties extends VectorGroupProps, LayerProps {}

/**
 * @internal
 */
export class VectorGroup extends Layer<VectorGroupProperties, {}> {
    static defaultVectorGroupProps: VectorGroupProps = {
        name: undefined,
        opacity: undefined,
        visible: true,
        x: 0,
        y: 0,
        rotation: 0,
        width: 100,
        height: 100,
        targetName: undefined,
        defaultName: "",
        isRootVectorNode: false,
        includeTransform: undefined,
        frame: { x: 0, y: 0, width: 100, height: 100 },
    }

    static readonly defaultProps: VectorGroupProperties = {
        ...Layer.defaultProps,
        ...VectorGroup.defaultVectorGroupProps,
    }

    render(): React.ReactElement<any> | null {
        if (process.env.NODE_ENV !== "production" && safeWindow["perf"]) safeWindow["perf"].nodeRender()

        const {
            id,
            name: nameProp,
            opacity,
            visible,
            targetName,
            defaultName,
            children,
            includeTransform,
            x,
            y,
            width,
            height,
            rotation,
            isRootVectorNode,
        } = this.props

        if (!visible) return null
        const { target } = RenderEnvironment

        const rect = { x, y, width, height }
        const transform = transformValues(rect, rotation, isRootVectorNode, includeTransform)

        const addNames = target === RenderTarget.preview
        let name: string | undefined = undefined
        if (addNames) {
            if (targetName) {
                name = targetName
            } else if (nameProp) {
                name = nameProp
            } else {
                name = defaultName
            }
        }

        return this.renderElement(
            <g transform={transformString(transform)} {...{ id, name, opacity }}>
                {children}
            </g>
        )
    }

    private renderElement(element: JSX.Element) {
        const { isRootVectorNode, width, height, frame, willChangeTransform } = this.props

        if (!isRootVectorNode) {
            return element
        } // else

        return <SVGRoot {...{ frame, width, height, willChangeTransform }}>{element}</SVGRoot>
    }
}
import * as React from "react"
import { Layer, LayerProps } from "./Layer"
import { RenderTarget } from "../types/RenderEnvironment"
import { FrameProps, DeprecatedCoreFrameProps, FrameWithMotion } from "./Frame"
import { isReactChild, isReactElement } from "../../utils/type-guards"
import { safeWindow } from "../../utils/safeWindow"
import { NewConstraintProperties } from "../types/NewConstraints"
import { MotionStyle, AnimateSharedLayout, LayoutGroupContext } from "framer-motion"
import { runtime } from "../../utils/runtimeInjection"
import { AutomaticLayoutIds } from "../../components/AnimateLayout/LayoutIdContext"
import { ComponentContainerContext } from "./ComponentContainerContext"

/**
 * @internal
 */
export interface ComponentContainerProps extends Partial<NewConstraintProperties> {
    style: MotionStyle
    visible: boolean
    componentIdentifier: string
    name?: string
}

/**
 * @internal
 */
export interface ComponentContainerState {
    lastError?: {
        // Used to re-probe component for errors (see render method).
        children: React.ReactNode
        name: string
        message: string
        componentStack: string[]
    }
}

/**
 * @internal
 */
export interface ComponentContainerProperties extends ComponentContainerProps, LayerProps {
    innerRef?: React.RefObject<HTMLDivElement>
}

/**
 * ComponentContainer has been refactored and moved into Vekter. However, we
 * still require a ComponentContainer for symbols that are imported into code
 * via canvas.tsx generation. Since imported symbols are only used as children
 * of another code component, they do not need generated layout ids, and do not
 * need to be measured for auto sizing. This file can be entirely removed when
 * symbols can no longer be imported into code. Likely when code as modules is
 * released.
 *
 * @deprecated
 * @internal
 */
export class DeprecatedComponentContainer extends Layer<
    ComponentContainerProperties & { __layoutId?: string },
    ComponentContainerState
> {
    static supportsConstraints = true
    state: ComponentContainerState = {}

    static defaultComponentContainerProps: ComponentContainerProps = {
        style: {},
        visible: true,
        componentIdentifier: "",
    }

    static readonly defaultProps: ComponentContainerProperties = {
        ...Layer.defaultProps,
        ...DeprecatedComponentContainer.defaultComponentContainerProps,
    }

    static contextType = ComponentContainerContext

    componentDidCatch(error: Error, info: React.ErrorInfo) {
        let stack = info.componentStack.split("\n").filter(line => line.length !== 0)
        let currentIndex = 0
        for (const line of stack) {
            if (line.startsWith(`    in ${this.constructor.name}`)) {
                break
            }
            currentIndex++
        }
        stack = stack.slice(0, currentIndex)
        this.setState({
            lastError: {
                children: this.props.children,
                name: error.name,
                message: error.message,
                componentStack: stack,
            },
        })
    }

    renderErrorPlaceholder(file: string, error: unknown): JSX.Element {
        const { RenderPlaceholder } = runtime
        return (
            <FrameWithMotion {...this.props} background={null}>
                <RenderPlaceholder error={{ error, file }} />
            </FrameWithMotion>
        )
    }

    render() {
        if (process.env.NODE_ENV !== "production" && safeWindow["perf"]) safeWindow["perf"].nodeRender()
        let { children } = this.props
        const { componentIdentifier, innerRef } = this.props
        const { lastError: error } = this.state

        // If the file of the component is in has a compile or load error, there will be no children
        // and there will be an error in the componentLoader. If so we render that error.
        // Note, cannot use React.Children.count when children = [null]
        const noChildren = !children || (Array.isArray(children) && children.filter(c => c).length === 0)
        if (noChildren) {
            const errorComponent = runtime.componentLoader.errorForIdentifier(componentIdentifier)
            if (errorComponent) {
                return this.renderErrorPlaceholder(errorComponent.file, errorComponent.error)
            }
        }

        // If an error occurred, componentDidCatch will set error. Additionally, we keep track of the child(ren)
        // reference of this container and only render the error when nothing changed. This means we will
        // re-render the component when something does change, which will either take us out of the error state
        // or update the children reference and keep showing the error. Effectively, this re-probes the component
        // for errors, without throwing an error twice in a row which would make React skip this error boundary
        // and go up the stack.
        if (error && error.children === children) {
            const component = runtime.componentLoader.componentForIdentifier(componentIdentifier)
            const file = component ? component.file : "???"
            return this.renderErrorPlaceholder(file, error.message)
        }

        // This is provided by the time budget logic in runtime
        safeWindow["__checkComponentBudget__"]?.()

        // FIXME: this suppresses warnings about Motion-backed types that aren't supported by the deprecated props.
        let frameProps = this.props as Partial<FrameProps & DeprecatedCoreFrameProps>

        if (RenderTarget.current() !== RenderTarget.canvas) {
            // For Code Overrides, we want the styling properties to be applied to the Frame,
            // and the rest to the actual component
            const {
                left,
                right,
                top,
                bottom,
                center,
                centerX,
                centerY,
                aspectRatio,
                parentSize,
                width,
                height,
                rotation,
                opacity,
                visible,
                _constraints,
                _initialStyle,
                name,
                positionSticky,
                positionStickyTop,
                positionStickyRight,
                positionStickyBottom,
                positionStickyLeft,
                // Remove the children and the componentIdentifier from the props passed into the component
                componentIdentifier: originalComponentIdentifier,
                children: originalChildren,
                style,
                duplicatedFrom,
                widthType,
                heightType,
                ...childProps
            } = frameProps as Partial<FrameProps & DeprecatedCoreFrameProps & ComponentContainerProperties>
            children = React.Children.map(originalChildren, (child: React.ReactElement<typeof childProps>) => {
                if (!isReactChild(child) || !isReactElement(child)) {
                    return child
                }

                // For framer-motion's `layout` to work inside code components,
                // they need to be wrapped in AnimateSharedLayout.
                // Additionally, code components need to avoid generating layout ids for canvas layers.
                if (!isPageOrScroll(originalComponentIdentifier)) {
                    return (
                        <LayoutGroupContext.Provider value={this.props.__layoutId ?? null}>
                            <AnimateSharedLayout>
                                <AutomaticLayoutIds enabled={false}>
                                    {React.cloneElement(child, childProps)}
                                </AutomaticLayoutIds>
                            </AnimateSharedLayout>
                        </LayoutGroupContext.Provider>
                    )
                }

                return React.cloneElement(child, childProps)
            })
            frameProps = {
                style,
                _constraints,
                _initialStyle,
                left,
                right,
                top,
                bottom,
                center,
                centerX,
                centerY,
                aspectRatio,
                parentSize,
                width,
                height,
                rotation,
                visible,
                name,
                duplicatedFrom,
                id: frameProps.id,
                layoutId: this.props.__layoutId,
                widthType,
                heightType,
                positionSticky,
                positionStickyTop,
                positionStickyRight,
                positionStickyBottom,
                positionStickyLeft,
            }
        }

        return (
            /* The background should come before the frameProps. It looks like there never should be a background in frameProps,
             * but published design components can contain an old version of the presentation tree that expects the background
             * that is passed to be rendered here
             * See the stackBackgroundTest.tsx integration test for an example of such a case
             */
            <ComponentContainerContext.Provider value>
                <FrameWithMotion
                    data-framer-component-container
                    background={null}
                    overflow="visible"
                    ref={innerRef}
                    {...frameProps}
                >
                    {children}
                </FrameWithMotion>
            </ComponentContainerContext.Provider>
        )
    }
}

function isPageOrScroll(identifier?: string) {
    if (!identifier) return false
    if (identifier === "framer/Page") return true
    if (identifier === "framer/Scroll") return true
    return false
}
import * as React from "react"
import { IdentityProps } from "./Layer"
import { Rect } from "../types/Rect"
import { Size } from "../types/Size"
import { ComponentIdentifier, SandboxReactComponentDefinition, ReactComponentDefinition } from "../componentLoader"
import { isEqual } from "../utils/isEqual"
import { isShallowEqualArray } from "../utils/isShallowEqualArray"

// Subset of CanvasNodeCache
/** @internal */
export interface PresentationTreeCache {
    props: any | null
    canvasZoom: number
    willChangeTransform: boolean
    reactElement: React.ReactNode | null
    codeComponentPresentation: CodeComponentPresentation | null
    placeholders: { index: number; sizes: Size[] } | null
}

/** @internal */
export interface PresentationTree {
    rect(parentSize: Size | null): Rect
    getProps(): IdentityProps
    children?: PresentationTree[]
    cache: Partial<PresentationTreeCache>
}

/**
 * @internal
 */
export function convertPresentationTree<C>(
    node: PresentationTree,
    converter: (node: PresentationTree, children: C[] | undefined) => C,
    componentDefinitionProvider: ReactComponentDefinitionProvider,
    getCachedNode?: (node: PresentationTree) => C | undefined,
    skipCodeComponentPropsCache: boolean = false
): C {
    // if there is a cached node, we use it without looking at any children etc.
    const cachedNode = getCachedNode && getCachedNode(node)
    if (cachedNode) return cachedNode

    // otherwise build children depth first and convert node
    let children: C[] | undefined
    if (isCodeComponentContainerPresentation(node)) {
        children = convertCodeComponentContainer(
            componentDefinitionProvider,
            node,
            converter,
            getCachedNode,
            skipCodeComponentPropsCache
        )
    } else if (node.children) {
        children = node.children.map(n =>
            convertPresentationTree(
                n,
                converter,
                componentDefinitionProvider,
                getCachedNode,
                skipCodeComponentPropsCache
            )
        )
    }
    return converter(node, children)
}

/**
 * A trimmed down version of the `componentLoader` interface
 *
 * @internal
 */
interface ReactComponentDefinitionProvider {
    reactComponentForIdentifier(
        identifier: ComponentIdentifier
    ): SandboxReactComponentDefinition | ReactComponentDefinition | null
}

interface CodeComponentContainerPresentation extends PresentationTree {
    id: string
    codeComponentIdentifier: string
    codeComponentPackageVersion: string | null
    codeOverrideIdentifier?: string
    getComponentChildren?: (componentDefinitionProvider: ReactComponentDefinitionProvider) => PresentationTree[]
    getCodeComponentProps?: (
        componentDefinitionProvider: ReactComponentDefinitionProvider,
        options?: { skipCache: boolean }
    ) => object
    getComponentSlotChildren?: (
        componentDefinitionProvider: ReactComponentDefinitionProvider
    ) => { [key: string]: PresentationTree[] }
}

function isCodeComponentContainerPresentation(value: PresentationTree): value is CodeComponentContainerPresentation {
    return !!(value as any).codeComponentIdentifier
}

function convertCodeComponentContainer<C>(
    componentDefinitionProvider: ReactComponentDefinitionProvider,
    node: CodeComponentContainerPresentation,
    converter: (node: PresentationTree, children: C[] | undefined) => C,
    getCachedNode?: (node: PresentationTree) => C | undefined,
    skipCodeComponentPropsCache: boolean = false
) {
    const codeComponentChildren = node.getComponentChildren
        ? node.getComponentChildren(componentDefinitionProvider)
        : []
    const codeComponentSlots = node.getComponentSlotChildren
        ? node.getComponentSlotChildren(componentDefinitionProvider)
        : {}

    let codeComponentPresentation: CodeComponentPresentation

    const props = node.getCodeComponentProps
        ? node.getCodeComponentProps(componentDefinitionProvider, { skipCache: skipCodeComponentPropsCache })
        : undefined

    if (node.cache.codeComponentPresentation) {
        codeComponentPresentation = node.cache.codeComponentPresentation
        if (!isShallowEqualArray(codeComponentPresentation.children, codeComponentChildren)) {
            codeComponentPresentation.cache.reactElement = null
            codeComponentPresentation.children = codeComponentChildren
        }
        if (!isEqual(codeComponentPresentation.props, props)) {
            codeComponentPresentation.cache.reactElement = null
            codeComponentPresentation.cache.props = null
            codeComponentPresentation.props = props
        }
    } else {
        const { id: containerId, codeComponentIdentifier: identifier, codeComponentPackageVersion } = node

        node.cache.codeComponentPresentation = codeComponentPresentation = new CodeComponentPresentation(
            containerId + identifier,
            identifier,
            codeComponentPackageVersion,
            props,
            codeComponentChildren
        )
    }

    codeComponentPresentation.props.placeholders = node.cache.placeholders

    const slotKeys = Object.keys(codeComponentSlots)

    if (slotKeys.length) {
        codeComponentPresentation.props = { ...codeComponentPresentation.props }
        codeComponentPresentation.props.__slotKeys = slotKeys

        for (const slotKey of slotKeys) {
            const slotChildren = codeComponentSlots[slotKey].map(child =>
                convertPresentationTree(
                    child,
                    converter,
                    componentDefinitionProvider,
                    getCachedNode,
                    skipCodeComponentPropsCache
                )
            )
            codeComponentPresentation.props[slotKey] = slotChildren
        }
    }

    return [
        converter(
            codeComponentPresentation,
            codeComponentPresentation.children.map(child =>
                convertPresentationTree(
                    child,
                    converter,
                    componentDefinitionProvider,
                    getCachedNode,
                    skipCodeComponentPropsCache
                )
            )
        ),
    ]
}

/** @internal */
class CodeComponentPresentation implements PresentationTree {
    cache: Partial<PresentationTreeCache> = {}

    constructor(
        private id: string,
        public componentIdentifier: string,
        public packageVersion: string | null,
        public props: any,
        public children: PresentationTree[],
        public codeOverrideIdentifier?: string
    ) {}

    getProps() {
        return {
            ...this.props,
            id: this.id,
            key: this.id,
        }
    }

    rect(parentSize: Size | null) {
        // N.B. This is never called.
        return { x: 0, y: 0, width: 0, height: 0 }
    }
}
